/* eslint-disable */
import {
  Box,
  Button,
  Card,
  CardContent,
  TextField,
  InputAdornment,
  SvgIcon
} from '@material-ui/core';
import { Search as SearchIcon } from 'react-feather';
import { useDishState } from '../../context/dishAppContext';

const OrdreListToolbar = (props) => {

  const {
    state: { cart }
  } = useDishState();

  const TotalPrice=cart.dishes.map(({price,quantity})=>{
    return price*quantity
  }).reduce((curNumber, item) => {
    return Number(curNumber) + Number(item);
  }, 0);
  console.log(TotalPrice)
  return (
    <Box {...props}>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-end'
        }}
      >
        <Button>Import</Button>
        <Button sx={{ mx: 1 }}>Export</Button>
        <Button color="primary" variant="contained">
          Order
        </Button>
      </Box>
      <Box sx={{ mt: 3,
      }}>
        <Card>
          <CardContent>
            <Box sx={{ maxWidth: 500 ,justifyContent: 'flex-end' }}>
              <TextField
                fullWidth
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SvgIcon fontSize="small" color="action">
                        <SearchIcon />
                      </SvgIcon>
                    </InputAdornment>
                  )
                }}
                placeholder="Search customer"
                variant="outlined"
              />
              <h3 >{TotalPrice}</h3>
            </Box>
          </CardContent>

        </Card>
      </Box>
    </Box>
  );
};

export default OrdreListToolbar;
