import api from '../config/base';

export const getAllDish = async () => {
  try {
    const { data } = await api.get('/');
    return data;
  } catch (err) {
    console.error(err);
  }
};
export const addDish = (DishData) => {
  api
    .post('/dish/add/', DishData)
    .then(({ data }) => data)
    .catch((err) => {
      throw Error(err.message);
    });
};

export const FindDishByCateg = (CategName) => {
  api
    .post('/dish/findByCateg', CategName)
    .then(({ data }) => data)
    .catch((err) => {
      console.error(err);
    });
};

export const FindDishById = (id) => {
  api
    .post(`/dish/${id}`)
    .then(({ data }) => data)
    .catch((err) => {
      console.error(err);
    });
};
