/* eslint-disable */
import {
  Box,
  Button,
  Card,
  CardContent,
  TextField,
  InputAdornment,
  SvgIcon
} from '@material-ui/core';
import { Search as SearchIcon } from 'react-feather';
import { useDishState,useDishDispatch } from '../../context/dishAppContext';
import {AddOrdre} from "../../services/DishServices"
import _ from 'loadsh'

const OrdreListToolbar = (props) => {

  const {
    state: { cart }
  } = useDishState();
  const dispatch = useDishDispatch();

  const TotalPrice=cart.dishes.map(({price,quantity})=>{
    return price*quantity
  }).reduce((curNumber, item) => {
    return Number(curNumber) + Number(item);
  }, 0);
  const onAddOrdre=async()=>{
   const data=await AddOrdre(cart.dishes)
   console.log(data)
   const grouped = _.groupBy(data.Info, car => car.ordred);

   const notOrdred=grouped.undefined.map(({notOrdred,quantity})=>{
     alert(notOrdred)
   })
  }
  return (
    <Box {...props}>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-end'
        }}
      >
        <Button color="primary" variant="contained" onClick={onAddOrdre}>
          Order Now {TotalPrice} â‚¬
        </Button>
      </Box>
      <Box sx={{ mt: 3,
      }}>
      </Box>
    </Box>
  );
};

export default OrdreListToolbar;
