/* eslint-disable */
import React,{useEffect} from 'react';
import {
  Box,
  Button,
  Card,
  CardContent,
  TextField,
  InputAdornment,
  SvgIcon
} from '@material-ui/core';
import { Search as SearchIcon } from 'react-feather';
import { useDishState,useDishDispatch } from '../../context/dishAppContext';

const OrdreListToolbar = (props) => {

  const {
    state: { cart }
  } = useDishState();
  const dispatch = useDishDispatch();
useEffect(() => {
  const TotalPrice=cart.dishes.map(({price,quantity})=>{
    return price*quantity
  }).reduce((curNumber, item) => {
    return Number(curNumber) + Number(item);
  }, 0);
  dispatch({ type: 'CALCULATIOTOTALPRICE', TotalPrice:TotalPrice });
}, [])

  return (
    <Box {...props}>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-end'
        }}
      >
        <Button>Import</Button>
        <Button sx={{ mx: 1 }}>Export</Button>
        <Button color="primary" variant="contained">
          Order Now {TotalPrice} â‚¬
        </Button>
      </Box>
      <Box sx={{ mt: 3,
      }}>
        <Card>
          <CardContent>
            <Box sx={{ maxWidth: 500 ,justifyContent: 'flex-end',display: 'flex', }}>
              <TextField
                fullWidth
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SvgIcon fontSize="small" color="action">
                        <SearchIcon />
                      </SvgIcon>
                    </InputAdornment>
                  )
                }}
                placeholder="Search customer"
                variant="outlined"
              />
            </Box>
          </CardContent>

        </Card>
      </Box>
    </Box>
  );
};

export default OrdreListToolbar;
