/* eslint-disable */
import React,{useState,useEffect} from 'react';
import { Helmet } from 'react-helmet';
import { Box, Container, Grid, Pagination } from '@material-ui/core';
import ProductListToolbar from '../components/product/ProductListToolbar';
import ProductCard from '../components/DishCard/DishCard';
import products from '../__mocks__/products';
import { getAllDish } from 'src/services/DishServices';
import moment from 'moment';

const ProductList =  () => {
  const [allDishes,setAllDishes]=useState([])

  const getDishes=async ()=>{
   const AllDishes= await getAllDish()
   setAllDishes(AllDishes)
  }
  useEffect(() => {
   getDishes()
  }, [])
  console.log(allDishes)
  console.log(products)
  const ItIsAviliable=()=>{
    allDishes.map(({availbility})=>{
    const av= availbility.map(({availbilityTime})=>{
       const startTime=new Date().setHours(availbilityTime.start,0,0)
       const endTime=new Date().setHours(availbilityTime.end,0,0)
       const date  = new Date()
      const range = moment(date).isBetween(startTime, endTime); 
       return range
     })
     console.log(av)
      })

  }
  ItIsAviliable()
  return (
    <>
      <Helmet>
        <title>Products | Material Kit</title>
      </Helmet>
      <Box
        sx={{
          backgroundColor: 'background.default',
          minHeight: '100%',
          py: 2
        }}
      >
        <Container maxWidth={false} justifyContent="center">
          <ProductListToolbar />
          <Box sx={{ pt: 3 }}>
            <Grid container spacing={3}>
              {allDishes.map((product) => (
                <Grid item key={product._id} lg={4} md={6} xs={12}>
                  <ProductCard product={product} />
                </Grid>
              ))}
            </Grid>
          </Box>
          <Box
            sx={{
              display: 'flex',
              justifyContent: 'center',
              pt: 3
            }}
          >
            <Pagination color="primary" count={3} size="small" />
          </Box>
        </Container>
      </Box>
    </>
  );
};

export default ProductList;
